const PlasmicDescendants = {
  root: [
    "root",
    "tsfMainNavigationHome",
    "features",
    "img",
    "tsfLiteFewerStockouts",
    "ul",
    "link",
    "tsfFooter",
    "blogPosts",
    "ctaBlock",
    "heroSection"
  ],

  tsfMainNavigationHome: ["tsfMainNavigationHome"],
  features: ["features", "img", "tsfLiteFewerStockouts"],
  img: ["img"],
  tsfLiteFewerStockouts: ["tsfLiteFewerStockouts"],
  ul: ["ul"],
  link: ["link"],
  tsfFooter: ["tsfFooter"],
  blogPosts: ["blogPosts"],
  ctaBlock: ["ctaBlock"],
  heroSection: ["heroSection"]
};

function makeNodeComponent(nodeName) {
  const func = function (props) {
    const { variants, args, overrides } = React.useMemo(
      () =>
        deriveRenderOpts(props, {
          name: nodeName,
          descendantNames: PlasmicDescendants[nodeName],
          internalArgPropNames: PlasmicHomepage__ArgProps,
          internalVariantPropNames: PlasmicHomepage__VariantProps
        }),
      [props, nodeName]
    );
    return PlasmicHomepage__RenderFunc({
      variants,
      args,
      overrides,
      forNode: nodeName
    });
  };
  if (nodeName === "root") {
    func.displayName = "PlasmicHomepage";
  } else {
    func.displayName = `PlasmicHomepage.${nodeName}`;
  }
  return func;
}

export const PlasmicHomepage = Object.assign(
  // Top-level PlasmicHomepage renders the root element
  makeNodeComponent("root"),
  {
    // Helper components rendering sub-elements
    tsfMainNavigationHome: makeNodeComponent("tsfMainNavigationHome"),
    features: makeNodeComponent("features"),
    img: makeNodeComponent("img"),
    tsfLiteFewerStockouts: makeNodeComponent("tsfLiteFewerStockouts"),
    ul: makeNodeComponent("ul"),
    link: makeNodeComponent("link"),
    tsfFooter: makeNodeComponent("tsfFooter"),
    blogPosts: makeNodeComponent("blogPosts"),
    ctaBlock: makeNodeComponent("ctaBlock"),
    heroSection: makeNodeComponent("heroSection"),
    // Metadata about props expected for PlasmicHomepage
    internalVariantProps: PlasmicHomepage__VariantProps,
    internalArgProps: PlasmicHomepage__ArgProps,
    // Page metadata
    pageMetadata: {
      title: "",
      description: "",
      ogImageSrc: "",
      canonical: ""
    }
  }
);

export default PlasmicHomepage;
